import { Amplify } from 'aws-amplify';
import type { WithAuthenticatorProps } from '@aws-amplify/ui-react';
import { withAuthenticator } from '@aws-amplify/ui-react';
import '@aws-amplify/ui-react/styles.css';
import config from './amplifyconfiguration.json';
import MintComponent from './components/Mint';
import MintDetail from './components/MintDetail';
import { useRoutes, useParams } from 'react-router-dom';

Amplify.configure(config);

export function App({ signOut, user }: WithAuthenticatorProps) {
  const { id }: { id: string } = useParams(); // Use useParams to get the id from the route

  const sendRequest = async () => {
    try {
      const headers = {
        Authorization: `Bearer ${test}`, // Ensure you define 'test' or replace it with your authentication token logic
      };

      const response = await fetch('https://your-backend-api-endpoint/resource', {
        method: 'GET',
        headers,
      });

      const data = await response.json();
      console.log('Backend response:', data);
    } catch (error) {
      console.error('Backend request error:', error);
    }
  };

  const routes = [
    {
      path: '/mint/:id',
      element: <MintDetail id={"1"} />, // Pass the id from useParams to MintDetail
    },
  ];

  const routing = useRoutes(routes);

  return (
    <>
      <h1>Hello {user?.username}</h1>
      <MintComponent />
      {routing}
      <button onClick={signOut}>Sign out</button>
      <button onClick={sendRequest}>Send Request to Backend</button>
    </>
  );
}

export default withAuthenticator(App);
