import React, { useEffect, useState } from 'react';

interface MintDetailProps {
    id: string;
}

const MintDetail: React.FC<MintDetailProps> = () => {
    const [mintDetail, setMintDetail] = useState<any>({
        name: 'Mock Mint',
        description: 'This is a mock mint detail.',
        image: 'https://example.com/mock-mint-image.jpg'
    });


    useEffect(() => {
        const fetchMintDetail = async () => {
            try {
                const response = await get(`/api/mints/${id}`); // Replace with your API endpoint
                const data = await response.json();
                setMintDetail(data);
            } catch (error) {
                console.error('Error fetching mint detail:', error);
            }
        };

        fetchMintDetail();
    }, [id]);

    if (!mintDetail) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>{mintDetail.name}</h2>
            <p>{mintDetail.description}</p>
            <img src={mintDetail.image} alt={mintDetail.name} />
        </div>
    );
};

export default MintDetail;
